(window.webpackJsonp=window.webpackJsonp||[]).push([[58],{331:function(t,s,n){"use strict";n.r(s);var r=n(14),v=Object(r.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h2",{attrs:{id:"读书分享"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#读书分享"}},[t._v("#")]),t._v(" 读书分享")]),t._v(" "),s("p",[t._v("看到这本书名，其实分成现代、西方思想，他们在历史长河中有着哪些让人共鸣的思想")]),t._v(" "),s("p",[t._v("第一章  启蒙")]),t._v(" "),s("p",[t._v("这一章看了两遍了，边看边忘，告诉自己自己思想深度不够，而不是记性太差。第一章使用韦伯的观点，引出了现代人的思想困境，具有启蒙意义，这个思想困境是由理性和信仰矛盾引起的。现代的最大变化是变化极快，让现代人有了一种不断接近真相，求知最终理想的想法，科学的进步让我们对周边、对人生有了新的认识和追求。然而，什么是真相，什么是最终真理，所有人都在思考（或者说没思考，想着规避，但是你真正能摆脱的了么？），但是对于人生的终极定义，不像我们所谓的客观真理，不是确定的，往往，这个不确定导致了所有引起的问题，我是谁？我在哪里？我将要去哪里？")]),t._v(" "),s("p",[t._v("第二章 现代人的思想危机")]),t._v(" "),s("p",[t._v("这一章读了三遍，感触极深，主要通过近代的三位思想家：尼采、佛洛依德、萨特。这三个人观点鲜明且有联系性，主要引导大家探索人生的终极意义。对于尼采最著名的一句“上帝死了”，到底谁杀死的，是好事坏事，我们以后干什么？对于我们杀死了上帝这个观点千人千面，感触最深的是：我们所谓的信仰没了，这个信仰值得是“神”，我们以前以为是“神”主宰一切，一切终究目标是为了“神”；但是现在理性思想告诉我们，”我们才是自己真正的神“，所以我们生活的意义是自己给与的；”如果你是我的信徒，你就不应该相信我的观点“，这句话看似矛盾，它的深层含义让人深有感触；佛洛依德通过”科学“的论证，证明人生的意义，给我们带来的全新的思考方式，但是实验和结果真能完全一致？这个有待我们思考；离我们最近的现代人是萨特，他给我的最深刻思想是，他把理论和实践结合一起，无论是他的婚姻，以及政治行为，他的一身贯彻着他独立自由的思想，他出名的一句话“存在即虚无”：想象一下杯子和服务员最大的区别是什么？杯子具有物化性，而人不可能一直是服务员，回家后是妻子是母亲，不同场景、不同的时空赋予了人不同的意义和价值，所以我们的终极意义是自己赋予的，这个虚无的目标是确切存在的。")]),t._v(" "),s("p",[t._v("因此在我们人生的旅途中，先有一个目标，这个目标可能是虚无的，可能是短视的，但是他是我们当前最好的价值意义，存在先于实现。")])])}),[],!1,null,null,null);s.default=v.exports}}]);